from Crypto.Util.number import isPrime, inverse, bytes_to_long
from random import randint
from flag import m


def lfsr(R, mask):
    out = (R << 1) & 0xffffff  # 24 bits
    bit = (R & mask) & 0xffffff
    out_bit = 0
    while bit != 0:
        out_bit ^= (bit & 1)
        bit = bit >> 1
    out ^= out_bit
    return out, out_bit


def genPrime(R, mask, bit):
    count = 0
    while True:
        prime = 0
        for i in range(bit):
            (R, out) = lfsr(R, mask)
            prime = (prime << 1) ^ out
        if isPrime(prime):
            yield prime


