def all_factor(n): # find all the factors
    factors = []
    limit = n//2 + 1 # ???
    for i in range(1, limit):
        if n % i == 0:
            factors.append(i)
    factors.append(n)
    return factors


def u(d):
    if d == 1:
        return 1
    factors = factorint(d)
    primes = list(factors.keys())
    index = list(factors.values())
    for i in index:
        if i >= 2:
            return 0
    return pow(-1, len(primes) % 2)


def f(d):
    factors = factorint(d)
    primes = list(factors.keys())
    index = list(factors.values())
    base = 1
    for i in range(len(primes)):
        base *= pow(primes[i], index[i]-1)
        base *= (primes[i] - 1)
    return base


def combine(n):
    factors = all_factor(n) # factors of n
    base = 0
    for i in factors:
        base += u(i) * f(i) #???
    return base


def Yusa(m):
    p = getPrime(533)
    q = getPrime(533)
    n = p * q
    _phi = (p - 1) * (q - 1)
    limit1 = (3 * n) // (2 * (int(iroot(n, 4)[0]) + 3 * (p + q)))

    while True:
        x = getPrime(256)
        y = getPrime(256)
        if x * y < limit1:
            break

    limit2 = (abs(p-q) * int(iroot(n, 4)[0]) * y) // (6 * (max(p, q)))
    e = (y * _phi) // x
    while True:
        z = e * x - y * _phi
        if abs(z) < limit2 and gcd(e, _phi) == 1:
            break
        e -= 1

    c = pow(m, e, n)
    return c, e, n


